import React, { useState } from "react";
import foodData from "../data/foodData";

const MenuPage = () => {
  const [selectedCategory, setSelectedCategory] = useState("всі");

  const categories = ["всі", "торти", "еклери", "круасан", "тістечка", "печево", "напої"];

  const filteredItems = selectedCategory === "всі"
    ? foodData
    : foodData.filter(item => item.category === selectedCategory);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Меню кондитерської</h2>

      <div style={{ marginBottom: "20px" }}>
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            style={{
              margin: "5px",
              padding: "8px 16px",
              backgroundColor: selectedCategory === cat ? "#ffb6c1" : "#e0e0e0",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer"
            }}
          >
            {cat}
          </button>
        ))}
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {filteredItems.map(item => (
          <div
            key={item.id}
            style={{
              width: "400px",
              border: "1px solid #ddd",
              borderRadius: "8px",
              padding: "10px",
              textAlign: "center",
              backgroundColor: "#fff"
            }}
          >
            <img
              src={`images/${item.image}`}
              alt={item.name}
              style={{ width: "100%", height: "150px", objectFit: "cover", borderRadius: "4px" }}
            />
            <h4>{item.name}</h4>
            <p>{item.price} грн</p>
            <button style={{ padding: "6px 12px", backgroundColor: "#f48fb1", border: "none", borderRadius: "4px" }}>
              Додати в кошик
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MenuPage;
