import React from "react";

const CartPage = ({ cartItems, onPlaceOrder }) => {
  return (
    <div style={{ padding: "20px" }}>
      <h2>Ваш кошик</h2>
      {cartItems.length === 0 ? (
        <p>Кошик порожній.</p>
      ) : (
        <div>
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                {item.name} — {item.price} грн
              </li>
            ))}
          </ul>
          <button
            onClick={onPlaceOrder}
            style={{
              marginTop: "20px",
              padding: "10px 20px",
              backgroundColor: "#f48fb1",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              color: "white",
            }}
          >
            Оформити замовлення
          </button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
